import React, { useState, useEffect } from 'react';
import moment from 'moment';
import { useParams, Link } from 'react-router-dom';

const HourPicker = () => {
  const { day } = useParams();
  const [busyHours, setBusyHours] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:5000/api/busy-hours/${day}`)
      .then(response => response.json())
      .then(data => setBusyHours(data))
      .catch(error => console.error('Error fetching busy hours:', error));
  }, [day]);

  const handleHourClick = hour => {
    saveSelectedHour(day, hour); // Dodajemy dzień jako pierwszy argument
  };

  const formatHour = hour => {
    return moment.utc(hour, 'HH:mm').format('HH:mm');
  };

  const saveSelectedHour = (day, hour) => {
    fetch(`http://localhost:5000/api/selected-hour/${day}/${hour}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      }
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      // Jeśli zapisanie godziny powiodło się, odśwież stronę
      window.location.reload();
    })
    .catch(error => console.error('Error saving selected hour:', error));
  };



  const renderHourButtons = () => {
    const hours = [];
    for (let hour = 9; hour < 18; hour++) {
      for (let minute = 0; minute < 60; minute += 30) {
        const formattedHour = moment.utc({ hour, minute }).format('HH:mm');
        let isHourBusy = false;

        for (const busyHour of busyHours) {
          if (formattedHour === formatHour(busyHour)) {
            isHourBusy = true;
            break;
          }
        }

        hours.push(
          <button
            key={formattedHour}
            onClick={() => handleHourClick(formattedHour)}
            style={{
              backgroundColor: isHourBusy ? 'red' : 'white',
              color: isHourBusy ? 'white' : 'black'
            }}
            disabled={isHourBusy}
          >
            {formattedHour}
          </button>
        );
      }
    }
    return hours;
  };

  return (
    <div className="hour-picker">
      <h4>Select hour for {day}:</h4>
      <div className="hour-buttons">{renderHourButtons()}</div>
      {renderBusyHours()}
      <Link to="/">Go to Home</Link>
    </div>
  );
};

export default HourPicker;
