// App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LoginForm from './LoginForm';
import HomePage from './HomePage';

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // Sprawdź, czy dane użytkownika są zapisane w pamięci podręcznej (np. localStorage)
    const savedUserData = localStorage.getItem('userData');
    if (savedUserData) {
      setUserData(JSON.parse(savedUserData));
      setLoggedIn(true);
    }
  }, []); // Uruchom tylko raz, po zamontowaniu komponentu

  const handleLogin = (userData) => {
    setUserData(userData);
    setLoggedIn(true);
    // Zapisz dane użytkownika w pamięci podręcznej (np. localStorage)
    localStorage.setItem('userData', JSON.stringify(userData));
  };

  const handleLogout = () => {
    setLoggedIn(false);
    setUserData(null);
    // Wyczyść dane użytkownika z pamięci podręcznej (np. localStorage)
    localStorage.removeItem('userData');
  };

  return (
    <Router>
      <div>
        <Routes>
          <Route path="/login" element={loggedIn ? <Navigate to="/" /> : <LoginForm handleLogin={handleLogin} />} />
          <Route path="/" element={loggedIn ? <HomePage userData={userData} handleLogout={handleLogout} /> : <Navigate to="/login" />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
