import React, { useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom'; // Import hook useLocation

const EditVisitPage = () => {
  const location = useLocation(); // Użyj hooka useLocation do pobrania obiektu location

  const [description, setDescription] = useState(''); // Stan dla opisu wizyty

  const handleSubmit = async () => {
    try {
      const visitId = location.state.visitId; // Pobranie id wizyty ze state
      await axios.put(`http://localhost:5000/api/edit-visit/${visitId}`, { description }); // Zapis opisu wizyty
      alert('Opis wizyty został zaktualizowany.'); // Wyświetlenie alertu po zapisie
    } catch (error) {
      console.error('Error updating visit description:', error);
      alert('Wystąpił błąd podczas aktualizacji opisu wizyty.'); // Wyświetlenie alertu w przypadku błędu
    }
  };

  return (
    <div>
      <h2>Edytuj wizytę</h2>
      <div>
        <label htmlFor="description">Opis wizyty:</label>
        <textarea id="description" value={description} onChange={(e) => setDescription(e.target.value)}></textarea>
      </div>
      <button onClick={handleSubmit}>Zapisz opis wizyty</button> {/* Przycisk do zapisu opisu wizyty */}
    </div>
  );
};

export default EditVisitPage;
