import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

const EditVisitPage = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const [description, setDescription] = useState('');

  useEffect(() => {
    // Sprawdzenie czy location.state nie jest null i czy visitId jest liczbą
    if (location.state && typeof location.state.visitId === 'number') {
      setDescription('');
    } else {
      // Przekierowanie użytkownika na stronę domową lub inną stronę, gdy location.state.visitId nie spełnia warunków
      navigate('/');
    }
  }, [location.state, navigate]);

  const handleSubmit = async () => {
    try {
      const visitId = location.state.visitId;
      await axios.put(`http://localhost:5000/api/edit-visit/${visitId}`, { description });
      navigate('/doctor-visits');
    } catch (error) {
      console.error('Error updating visit description:', error);
    }
  };

  return (
    <div>
      <h2>Edytuj wizytę</h2>
      <div>
        <label htmlFor="description">Opis wizyty:</label>
        <textarea id="description" value={description} onChange={(e) => setDescription(e.target.value)}></textarea>
      </div>
      <button onClick={handleSubmit}>Zapisz opis wizyty</button>
    </div>
  );
};

export default EditVisitPage;
