import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Menu.css'; // Zaimportuj plik ze stylami

const Menu = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [username, setUsername] = useState('');

  useEffect(() => {
  // Pobierz informacje o zalogowanym użytkowniku po zmianie stanu zalogowania
  fetch('http://localhost:5000/api/user')
    .then(response => response.json())
    .then(data => {
      if (data.loggedIn) {
        setLoggedIn(true);
        setUsername(data.username);
      } else {
        setLoggedIn(false);
        setUsername('');
      }
    })
    .catch(error => console.error('Error:', error));
}, [loggedIn]); // Dodaj loggedIn do zależności useEffect


  const handleLogout = () => {
    // Tutaj umieść kod do wylogowania użytkownika
    console.log('Wyloguj');
  };

  return (
    <nav className="menu">
      <div className="menu-links"> {/* Dodaj kontener dla linków */}
        {/* Dodaj linki do strony głównej, profilu i wylogowania */}
        <Link to="/" className="menu-link">Strona główna</Link>
        {loggedIn ? (
          <>
            <Link to="/" className="menu-link">Profil ({username})</Link>
            <button onClick={handleLogout} className="menu-link">Wyloguj</button>
          </>
        ) : (
          <>
            <Link to="/register" className="menu-link">Rejestracja</Link>
            <Link to="/login" className="menu-link">Logowanie</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Menu;
