import React, { useState } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';

const EditVisitPage = () => {
  const location = useLocation();
  const navigate = useNavigate();

  const [description, setDescription] = useState('');

  const handleSubmit = async () => {
    try {
      const visitId = location.state.visitId;
      await axios.put(`http://localhost:5000/api/edit-visit/${visitId}`, { description });
      navigate('/doctor-visits');
    } catch (error) {
      console.error('Error updating visit description:', error);
    }
  };

  // Sprawdzenie czy visitId jest przekazane, czy jest liczbą i czy nie jest nullem
  if (location.state.visitId === undefined || isNaN(location.state.visitId) || location.state.visitId === null) {
    // Przekierowanie użytkownika na stronę domową lub inną stronę, gdy visitId nie spełnia warunków
    navigate('/');
    return null;
  }

  return (
    <div>
      <h2>Edytuj wizytę</h2>
      <div>
        <label htmlFor="description">Opis wizyty:</label>
        <textarea id="description" value={description} onChange={(e) => setDescription(e.target.value)}></textarea>
      </div>
      <button onClick={handleSubmit}>Zapisz opis wizyty</button>
    </div>
  );
};

export default EditVisitPage;
