// LoginForm.js
import React, { useState } from 'react';
import { Navigate } from 'react-router-dom';
import axios from 'axios';

const LoginForm = ({ handleLogin }) => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Wysłanie żądania POST na serwer
      const response = await axios.post('http://localhost:5000/login', {
        email,
        password
      });
      
      // Przekazanie danych użytkownika do App po udanym zalogowaniu
      handleLogin(response.data);

      // Przekierowanie użytkownika na inną stronę po zalogowaniu
      Navigate('/');

    } catch (error) {
      setError(error.response.data.message); // Wyświetlenie błędu zwróconego przez serwer
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Logowanie</h2>
      {error && <div>{error}</div>}
      <div>
        <label>
          Adres e-mail:
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </label>
      </div>
      <div>
        <label>
          Hasło:
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </label>
      </div>
      <button type="submit">Zaloguj</button>
    </form>
  );
};

export default LoginForm;
