// HourPicker.js

import React from 'react';
import moment from 'moment';
import { useParams, Link } from 'react-router-dom'; // Importuj useParams hook do uzyskania dostępu do parametrów URL

const HourPicker = ({ onSelectHour }) => {
  const { day } = useParams(); // Pobierz parametr 'day' z adresu URL
  const handleHourClick = (hour) => {
    onSelectHour(hour);
  };

  const renderHourButtons = () => {
    const hours = [];
    for (let hour = 9; hour < 18; hour++) {
      for (let minute = 0; minute < 60; minute += 30) {
        const formattedHour = moment({ hour, minute }).format('HH:mm');
        hours.push(
          <button key={formattedHour} onClick={() => handleHourClick(formattedHour)}>
            {formattedHour}
          </button>
        );
      }
    }
    return hours;
  };

  return (
    <div className="hour-picker">
      <h4>Select hour for {day}:</h4> {/* Wyświetl wybrany dzień z adresu URL */}
      <div className="hour-buttons">{renderHourButtons()}</div>
      <Link to="/">Go to Home</Link> {/* Link do strony głównej */}
      <Link to="/calendar">Go back to Calendar</Link> {/* Link wróć do kalendarza */}
    </div>
  );
};

export default HourPicker;
