// Calendar.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Calendar = () => {
  const [year, setYear] = useState(new Date().getFullYear());
  const [month, setMonth] = useState(new Date().getMonth() + 1);
  const [calendarData, setCalendarData] = useState([]);

  useEffect(() => {
    fetchData();
  }, [year, month]);

  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/calendar?year=${year}&month=${month}`);
      setCalendarData(response.data);
    } catch (error) {
      console.error('Error fetching calendar data:', error);
    }
  };

  const daysInMonth = (year, month) => {
    return new Date(year, month, 0).getDate();
  };

  const generateCalendar = () => {
    const days = [];
    const totalDays = daysInMonth(year, month);

    for (let i = 1; i <= totalDays; i++) {
      days.push(i);
    }

    return days;
  };

  const generateTable = () => {
    const weeks = [];
    const calendar = generateCalendar();
    const firstDayOfMonth = new Date(year, month - 1, 1).getDay(); // Dzień tygodnia pierwszego dnia miesiąca

    let currentWeek = [];
    for (let i = 0; i < firstDayOfMonth; i++) {
      currentWeek.push('');
    }

    calendar.forEach((day, index) => {
      currentWeek.push(day);
      if ((firstDayOfMonth + index + 1) % 7 === 0 || index === calendar.length - 1) {
        weeks.push(currentWeek);
        currentWeek = [];
      }
    });

    return weeks;
  };

  return (
    <div>
      <h1>Calendar</h1>
      <h2>{`${year}-${month}`}</h2>
      <table>
        <tbody>
          {generateTable().map((week, weekIndex) => (
            <tr key={weekIndex}>
              {week.map((day, dayIndex) => (
                <td key={dayIndex}>
                  {day !== '' && day}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Calendar;
