import React, { useState } from 'react';
import axios from 'axios';
import { useLocation, Navigate } from 'react-router-dom'; // Zmieniamy useHistory na Navigate i usuwamy import useHistory

const EditVisitPage = () => {
  const location = useLocation();

  const [description, setDescription] = useState('');

  const handleSubmit = async () => {
    try {
      const visitId = location.state.visitId;
      await axios.put(`http://localhost:5000/api/edit-visit/${visitId}`, { description });
      return <Navigate to="/doctor-visits" replace />; // Przekierowanie użytkownika po udanej edycji do /doctor-visits
    } catch (error) {
      console.error('Error updating visit description:', error);
    }
  };

  return (
    <div>
      <h2>Edytuj wizytę</h2>
      <div>
        <label htmlFor="description">Opis wizyty:</label>
        <textarea id="description" value={description} onChange={(e) => setDescription(e.target.value)}></textarea>
      </div>
      <button onClick={handleSubmit}>Zapisz opis wizyty</button>
    </div>
  );
};

export default EditVisitPage;
