import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LoginForm from './LoginForm';
import HomePage from './HomePage';
import ChangePassword from './ChangePassword'; // Dodaj import

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    // Sprawdzenie czy użytkownik jest zalogowany po odświeżeniu strony
    const token = localStorage.getItem('token');
    if (token) {
      setLoggedIn(true);
      // Pobranie danych użytkownika, gdyż mogą być potrzebne na innych stronach
      // Tutaj można wywołać odpowiedni endpoint na serwerze, aby pobrać dane użytkownika
      const user = JSON.parse(localStorage.getItem('user'));
      setUserData(user);
    }
  }, []);

  const handleLogin = (userData) => {
    setUserData(userData);
    setLoggedIn(true);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setLoggedIn(false);
    setUserData(null);
  };

  return (
    <Router>
      <div>
        <Routes>
          <Route path="/login" element={loggedIn ? <Navigate to="/" /> : <LoginForm handleLogin={handleLogin} />} />
          <Route path="/changepassword" element={loggedIn ? <ChangePassword userData={userData} /> : <Navigate to="/login" />} />
          <Route path="/" element={loggedIn ? <HomePage userData={userData} handleLogout={handleLogout} /> : <Navigate to="/login" />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
