// Calendar.js

import React from 'react';
import './Calendar.css';
import moment from 'moment';

const Calendar = ({ calendarDays }) => {
  const currentDate = moment();
  const month = currentDate.format('MMMM');
  const year = currentDate.format('YYYY');

  const generateDaysInMonth = () => {
    const daysInMonth = moment().daysInMonth();
    const firstDayOfWeek = moment().startOf('month').weekday();

    const days = [];
    let currentWeek = [];

    for (let i = 0; i < firstDayOfWeek; i++) {
      currentWeek.push(<td key={`empty-${i}`} className="calendar-day"></td>);
    }

    for (let i = 1; i <= daysInMonth; i++) {
      const currentDay = moment(`${year}-${month}-${i}`, 'YYYY-MMMM-D');

      const isDayInDatabase = calendarDays.some(day => {
        return moment(day).isSame(currentDay, 'day');
      });

      currentWeek.push(
        <td key={`day-${i}`} className={`calendar-day ${isDayInDatabase ? 'red' : ''}`}>
          {i}
        </td>
      );

      if ((firstDayOfWeek + i) % 7 === 0 || i === daysInMonth) {
        days.push(<tr key={`week-${days.length}`}>{currentWeek}</tr>);
        currentWeek = [];
      }
    }

    return days;
  };

  return (
    <div className="calendar-container">
      <h2>{`${month} ${year}`}</h2>
      <table className="calendar-table">
        <tbody>{generateDaysInMonth()}</tbody>
      </table>
    </div>
  );
};

export default Calendar;
