import React, { useState, useEffect } from 'react';
import moment from 'moment';
import { useParams, Link } from 'react-router-dom';

const HourPicker = ({ onSelectHour }) => {
  const { day } = useParams();
  const [busyHours, setBusyHours] = useState([]);

  useEffect(() => {
    // Pobierz zajęte godziny dla danego dnia z backendu
    fetch(`http://localhost:5000/api/busy-hours/${day}`)
      .then(response => response.json())
      .then(data => setBusyHours(data))
      .catch(error => console.error('Error fetching busy hours:', error));
  }, [day]);

  const handleHourClick = hour => {
    onSelectHour(hour);
  };

  const renderHourButtons = () => {
    const hours = [];
    for (let hour = 9; hour < 18; hour++) {
      for (let minute = 0; minute < 60; minute += 30) {
        const formattedHour = moment({ hour, minute }).format('HH:mm');
        const isHourBusy = busyHours.includes(formattedHour); // Sprawdź, czy godzina jest zajęta
        hours.push(
          <button
            key={formattedHour}
            onClick={() => handleHourClick(formattedHour)}
            style={{ backgroundColor: isHourBusy ? 'red' : 'white' }} // Dostosuj styl przycisku na podstawie zajętości godziny
            disabled={isHourBusy} // Wyłącz przycisk, jeśli godzina jest zajęta
          >
            {formattedHour}
          </button>
        );
      }
    }
    return hours;
  };

  return (
    <div className="hour-picker">
      <h4>Select hour for {day}:</h4>
      <div className="hour-buttons">{renderHourButtons()}</div>
      <Link to="/">Go to Home</Link>
    </div>
  );
};

export default HourPicker;
