import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import LoginForm from './LoginForm';
import HomePage from './HomePage';
import ChangePassword from './ChangePassword';
import RegisterForm from './RegisterForm';
import Menu from './Menu'; // Importujemy komponent Menu

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      setLoggedIn(true);
      const user = JSON.parse(localStorage.getItem('user'));
      setUserData(user);
    }
    setLoading(false);
  }, []);

  const handleLogin = (userData) => {
    setUserData(userData);
    setLoggedIn(true);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setLoggedIn(false);
    setUserData(null);
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <Router>
      <div>
        {/* Renderujemy Menu na wszystkich stronach, z wyjÄ…tkiem /login i /register */}
        <Route element={<Menu loggedIn={loggedIn} handleLogout={handleLogout} />} />

        <Routes>
          <Route path="/login" element={loggedIn ? <Navigate to="/" /> : <LoginForm handleLogin={handleLogin} />} />
          <Route path="/register" element={!loggedIn ? <RegisterForm /> : <Navigate to="/" />} />
          <Route path="/changepassword" element={loggedIn ? <ChangePassword userData={userData} /> : <Navigate to="/login" />} />
          <Route path="/" element={loggedIn ? <HomePage userData={userData} handleLogout={handleLogout} /> : <Navigate to="/login" />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
