// Calendar.js

import React, { useState, useEffect } from 'react';
import './Calendar.css';
import axios from 'axios';

const Calendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [calendarDays, setCalendarDays] = useState([]);

  useEffect(() => {
    // Pobierz dni z backendu i ustaw je w stanie
    axios.get('/api/calendar')
      .then(response => {
        setCalendarDays(response.data.map(day => new Date(day.date)));
      })
      .catch(error => {
        console.error('Error fetching calendar days:', error);
      });
  }, []);

  const prevMonth = () => {
    setCurrentDate(prevDate => {
      const prevMonthDate = new Date(prevDate);
      prevMonthDate.setMonth(prevMonthDate.getMonth() - 1);
      return prevMonthDate;
    });
  };

  const nextMonth = () => {
    setCurrentDate(prevDate => {
      const nextMonthDate = new Date(prevDate);
      nextMonthDate.setMonth(nextMonthDate.getMonth() + 1);
      return nextMonthDate;
    });
  };

  const month = currentDate.toLocaleString('default', { month: 'long' });
  const year = currentDate.getFullYear();

  const generateDaysInMonth = () => {
    const firstDayOfMonth = new Date(year, currentDate.getMonth(), 1);
    const lastDayOfMonth = new Date(year, currentDate.getMonth() + 1, 0);
    const daysInMonth = lastDayOfMonth.getDate();
    const firstDayOfWeek = firstDayOfMonth.getDay();

    const days = [];

    for (let i = firstDayOfWeek; i < 7; i++) {
      days.push(<td key={`empty-${i}`} className="calendar-day"></td>);
    }

    for (let i = 1; i <= daysInMonth; i++) {
      const currentDate = new Date(year, month - 1, i);
      const isDayInDatabase = calendarDays.some(day => day.toDateString() === currentDate.toDateString());

      days.push(
        <td key={`day-${i}`} className={`calendar-day ${isDayInDatabase ? 'red' : ''}`}>
          {i}
        </td>
      );
    }

    return days;
  };

  return (
    <div className="calendar-container">
      <button onClick={prevMonth}>Previous Month</button>
      <h2>{`${month} ${year}`}</h2>
      <button onClick={nextMonth}>Next Month</button>
      <table className="calendar-table">
        <tbody>{generateDaysInMonth()}</tbody>
      </table>
    </div>
  );
};

export default Calendar;
