import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Visits = ({ userData }) => {
  const [visits, setVisits] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchVisits = async () => {
      try {
        if (userData) {
          const response = await axios.get(`http://localhost:5000/api/visits/${userData.email}`);
          const formattedVisits = response.data.map(visit => ({
            ...visit,
            date: formatDate(visit.date) // Formatujemy datę dla każdej wizyty
          }));
          setVisits(formattedVisits);
        } else {
          setVisits([]);
        }
        setLoading(false);
      } catch (error) {
        console.error('Error fetching visits:', error);
        setLoading(false);
      }
    };

    fetchVisits();

  }, [userData]);

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = ('0' + (date.getMonth() + 1)).slice(-2);
    const day = ('0' + date.getDate()).slice(-2);
    return `${year}-${month}-${day}`;
  };

  if (loading) {
    return <div>Ładowanie...</div>;
  }

  return (
    <div>
      <h2>Moje Wizyty</h2>
      {visits.length === 0 ? (
        <p>Brak wizyt</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Data</th>
              <th>Godzina</th>
            </tr>
          </thead>
          <tbody>
            {visits.map((visit, index) => (
              <tr key={index}>
                <td>{visit.date}</td>
                <td>{visit.time}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Visits;
