// Calendar.js
import React, { useState } from 'react';
import './Calendar.css';
import moment from 'moment';
import HourPicker from './HourPicker';
import { Redirect } from 'react-router-dom'; // Importujemy Redirect

const Calendar = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDay, setSelectedDay] = useState(null);
  const [selectedHour, setSelectedHour] = useState(null);
  const [redirect, setRedirect] = useState(false); // Stan dla przekierowania

  const handleDayClick = (day) => {
    setSelectedDay(day);
    setSelectedHour(null); // Resetowanie wybranej godziny po zmianie dnia
  };

  const handleHourSelect = (hour) => {
    setSelectedHour(hour);
    setRedirect(true); // Ustawienie stanu przekierowania po wybraniu godziny
  };

  const month = currentDate.toLocaleString('default', { month: 'long' });
  const year = currentDate.getFullYear();

  const generateDaysInMonth = () => {
    const firstDayOfMonth = new Date(year, currentDate.getMonth(), 1);
    const lastDayOfMonth = new Date(year, currentDate.getMonth() + 1, 0);
    const daysInMonth = lastDayOfMonth.getDate();
    const firstDayOfWeek = firstDayOfMonth.getDay();
    const today = moment().format('YYYY-MM-DD');

    const days = [];
    let currentWeek = [];

    for (let i = 0; i < firstDayOfWeek; i++) {
      currentWeek.push(<td key={`empty-${i}`} className="calendar-day"></td>);
    }

    for (let i = 1; i <= daysInMonth; i++) {
      const currentDay = moment(new Date(year, currentDate.getMonth(), i)).format('YYYY-MM-DD');
      const isDayInDatabase = calendarDays.includes(currentDay);
      const isFutureDay = currentDay > today;

      currentWeek.push(
        <td key={`day-${i}`} className={`calendar-day ${isDayInDatabase ? 'red' : ''}`}>
          {isFutureDay ? (
            <button onClick={() => handleDayButtonClick(currentDay)}>{i}</button>
          ) : (
            i
          )}
        </td>
      );

      if (currentWeek.length === 7 || i === daysInMonth) {
        days.push(<tr key={`week-${days.length}`}>{currentWeek}</tr>);
        currentWeek = [];
      }
    }

    return days;
  };

  // Wykonaj przekierowanie jeśli jest ustawione
  if (redirect) {
    return <Redirect to="/confirmation" />;
  }

  return (
    <div className="calendar-container">
      <div className="calendar">
        <h2>{`${month} ${year}`}</h2>
        {/* Tutaj generujesz kalendarz */}
      </div>
      <div className="reservation-options">
        {selectedDay && (
          <div>
            <h3>Selected Day: {moment(selectedDay).format('YYYY-MM-DD')}</h3>
            <HourPicker onSelectHour={handleHourSelect} />
            <p>Selected Hour: {selectedHour}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Calendar;
